import * as React from 'react';

function Card({ item, handleSelectedCards, isFirstCard, isDisabled, isWrongCard }) { 
    if (item?.isMatched) { return <div></div>}

    return ( 
        <div > 
            <button
                disabled={isDisabled}
                className={isFirstCard ? "first-card" : isWrongCard ? "wrong-card" : "back"}
                onClick={() => handleSelectedCards(item)} 
            >
            {item?.name} 
            </button>
        </div> 
    ); 
} 

export default Card;
