import React, { useState, useEffect } from "react";
import CardsData from "../data";
import Card from './Card';

interface ICard{
    id?: number,
    name?: string,
    country?: string,
    capital?: string,
    isMatched?: boolean
}

function QuizBoard() {
    let [cardsArray, setCardsArray] = useState<ICard[]>();
    // kep first card info
    let [firstCard, setFirstCard] = useState<ICard>();
    // is it first click?
    let [secondCard, setSecondCard] = useState<ICard>();

    // functions
    const handleSelectedCards = async (card) => {
        console.log("card", card)
        console.log("check1")
        console.log("firstCard", firstCard)
        console.log("secondCard", secondCard)
        if (firstCard && Object.keys(firstCard).length > 0 && card.id !== firstCard?.id) {
            setSecondCard(card); 
        } else { 
            setFirstCard(card);
        }
  };

    const shuffle = (array) => {
        let currentIndex = array.length;

        // While there remain elements to shuffle...
        while (currentIndex != 0) {

        // Pick a remaining element...
        let randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex--;

        // And swap it with the current element.
        [array[currentIndex], array[randomIndex]] = [
            array[randomIndex], array[currentIndex]];
        }
    }

    const NewGame = () => { 
        setTimeout(() => { 
            setCardsArray([]);
            const tempArray = CardsData;
            shuffle(tempArray);
            setCardsArray(tempArray); 
            setFirstCard({}); 
            setSecondCard({}); 
        }, 1200); 
    }

    const removeSelection = () => { 
        setFirstCard({}); 
        setSecondCard({}); 
    }

    useEffect(() => { 
        NewGame(); 
    }, []); 

    // useEffect(() => { 
    //     if (firstCard && secondCard) { 
    //         if (firstCard.country === secondCard.country || firstCard.capital == secondCard.capital) { 
    //             // setCardsArray((prevArray) => { 
    //             //     if (!prevArray){
    //             //         return prevArray;
    //             //     }

    //             //     return prevArray.map((item) => { 
    //             //         if (item.id === firstCard?.id || item.id === secondCard?.id) { 
    //             //             return { ...item, isMatched: true }; 
    //             //         } else { 
    //             //             return item; 
    //             //         } 
    //             //     }); 
    //             // }); 

    //             removeSelection(); 
    //         } else { 
    //             setTimeout(() => { 
    //                 removeSelection(); 
    //             }, 1000); 
    //         } 
    //     } 
    // }, [firstCard, secondCard]); 

    return (
        <div className="container"> 
            <div className="header"> 
                <h1>Capital Quiz</h1> 
            </div> 
            <div className="board"> 
                { 
                    // cards component help in coverting the  
                    // data from array to visible data for screen 
                    cardsArray?.map((item) => ( 
                        <Card 
                            item={item} 
                            key={item.id} 
                            handleSelectedCards={handleSelectedCards} 
                            isFirstCard={item === firstCard} 
                        /> 
                    )) 
                } 
            </div> 

            {(cardsArray?.length == 0) ? ( 
               <div className="comments"> 
               Congratulations!
           </div> 
            ) : ( 
                <div></div>
            )} 
            <button className="button" onClick={NewGame}> 
                New Game 
            </button> 
        </div> 
      ); 
}

export default QuizBoard;
