import React, { useState } from "react";
import CardsData from "../data";
import Card from './Card';

interface ICard{
    id?: number,
    name?: string,
    country?: string,
    capital?: string
}

function QuizBoard() {
    let [cardsArray, setCardsArray] = useState<ICard[]>();
    // kep first card info
    let [firstCard, setFirstCard] = useState<ICard>();
    // is it first click?
    let [secondCard, setSecondCard] = useState<ICard>();
    // set flag to wait for 3000ms
    const [won, setWon] = React.useState(0); 


    // functions
    const handleSelectedCards = async (card) => {
        if (firstCard !== null && card.id != firstCard?.id) {
            setSecondCard(card); 
        } else { 
            setFirstCard(card);
        }
  };

    const NewGame = () => { 
        setTimeout(() => { 
            setCardsArray([]); 
            const randomOrderArray = CardsData.sort(() => 0.5 - Math.random()); 
            setCardsArray(randomOrderArray); 
            setFirstCard({}); 
            setSecondCard({}); 
            setWon(0); 
        }, 1200); 
    }

    const removeSelection = () => { 
        setFirstCard({}); 
        setSecondCard({}); 
    }

    React.useEffect(() => { 
        NewGame(); 
    }, []); 

  React.useEffect(() => { 
    if (firstCard && secondCard) { 
        if (firstCard.name === secondCard.country || firstCard.capital == secondCard.name) { 
            // setCardsArray((prevArray) => { 
            //     return prevArray.map((item) => { 
            //         if (item?.id ==! firstCard?.id && item.id ==! secondCard.id) { 
            //             return item; 
            //         }
            //     }); 
            // }); 
            setWon((preVal) => preVal + 1); 
            removeSelection(); 
        } else { 
            setTimeout(() => { 
                removeSelection(); 
            }, 1000); 
        } 
    } 
}, [firstCard, secondCard]); 

    return (
        <div className="container"> 
            <div className="header"> 
                <h1>Capital Quiz</h1> 
            </div> 
            <div className="board"> 
                { 
                    // cards component help in coverting the  
                    // data from array to visible data for screen 
                    cardsArray?.map((item) => ( 
                        <Card 
                            item={item} 
                            key={item.id} 
                            handleSelectedCards={handleSelectedCards} 
                            toggled={ 
                                item === firstCard || 
                                item === secondCard
                            } 
                        /> 
                    )) 
                } 
            </div> 

            {won !== cardsArray?.length ? ( 
                <div></div> 
            ) : ( 
                <div className="comments"> 
                    Congratulations!
                </div> 
            )} 
            <button className="button" onClick={NewGame}> 
                New Game 
            </button> 
        </div> 
      ); 
}

export default QuizBoard;
