import React, { useState, useEffect } from "react";
import Card from '../Card';
import removeCardsFromArray from './logic/removeCardsFromArray';
import changeIsWrongForSelectedCards from "./logic/changeIsWrongForSelectedCards";
import Header from "../Header";
import WinningBanner from "../WinningBanner";
import ICard from "../../interfaces/CardInterface";
import getCountriesList from "../../server/getCountriesList";

function QuizBoard() {
    let [cardsArray, setCardsArray] = useState<ICard[] | null>();
    let [firstCard, setFirstCard] = useState<ICard | null>();
    let [secondCard, setSecondCard] = useState<ICard | null>();
    let [toDisable, setToDisable] = useState<boolean>();

    const handleSelectedCards = async (card) => {
        firstCard && card.id !== firstCard?.id ?
            setSecondCard(card) :  
            setFirstCard(card);
    };

    const removeSelection = () => { 
        setFirstCard(null); 
        setSecondCard(null); 
    }

     useEffect(() => { 
        setTimeout(async () => { 
            const cardsData = await getCountriesList();
            console.log("cardsData", cardsData)
            setCardsArray(cardsData); 
            setFirstCard(null); 
            setSecondCard(null); 
            setToDisable(false);
        }, 10); 
    }, []); 

    useEffect(() => { 
        if (firstCard && secondCard) { 
            if (firstCard.country === secondCard.country || firstCard.capital === secondCard.capital) { 
                setCardsArray(removeCardsFromArray(cardsArray, firstCard, secondCard)); 
                removeSelection(); 
            } else { 
                setCardsArray(changeIsWrongForSelectedCards(cardsArray, true, firstCard, secondCard))
                setToDisable(true);

                setTimeout(() => {
                    setToDisable(false);
                    setCardsArray(changeIsWrongForSelectedCards(cardsArray, false, firstCard, secondCard))
                    removeSelection(); 
                }, 3000); 
            } 
        } 
    }, [firstCard, secondCard]); 

    return (
        <div className="container"> 
            <Header/>
            <div className="board"> 
                {
                    cardsArray?.map((item) => (
                        <Card 
                            item={item} 
                            key={item.id} 
                            handleSelectedCards={handleSelectedCards} 
                            isFirstCard={item === firstCard} 
                            isDisabled={toDisable} 
                        />
                    )) 
                } 
            </div> 

            {(cardsArray?.filter((item) => { return !item.isMatched }).length === 0) ? ( 
               <WinningBanner/>
            ) : null} 
        </div> 
      ); 
}

export default QuizBoard;
