import React, { useState, useEffect } from "react";
import CardsData from "../data";
import Card from './Card';

interface ICard{
    id?: number,
    name?: string,
    country?: string,
    capital?: string,
    isMatched?: boolean,
    isWrong?: boolean
}

function QuizBoard() {
    let [cardsArray, setCardsArray] = useState<ICard[] | null>();
    let [firstCard, setFirstCard] = useState<ICard | null>();
    let [secondCard, setSecondCard] = useState<ICard | null>();
    let [toDisable, setToDisable] = useState<boolean>();

    // functions
    const handleSelectedCards = async (card) => {
        console.log("card", card)
        console.log("check1")
        console.log("firstCard", firstCard)
        console.log("secondCard", secondCard)
        if (firstCard && Object.keys(firstCard).length > 0 && card.id !== firstCard?.id) {
            setSecondCard(card); 
        } else { 
            setFirstCard(card);
        }
  };

    const shuffle = (array) => {
        let currentIndex = array.length;

        // While there remain elements to shuffle...
        while (currentIndex != 0) {

        // Pick a remaining element...
        let randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex--;

        // And swap it with the current element.
        [array[currentIndex], array[randomIndex]] = [
            array[randomIndex], array[currentIndex]];
        }
    }

    const NewGame = () => { 
        setTimeout(() => { 
            setCardsArray([]);
            const tempArray = CardsData;
            shuffle(tempArray);
            setCardsArray(tempArray); 
            setFirstCard(null); 
            setSecondCard(null); 
            setToDisable(false);
        }, 1200); 
    }

    const removeSelection = () => { 
        setFirstCard(null); 
        setSecondCard(null); 
    }

    useEffect(() => { 
        NewGame(); 
    }, []); 

    useEffect(() => { 
        if (firstCard && secondCard) { 
            if (firstCard.country === secondCard.country || firstCard.capital === secondCard.capital) { 
                const removeCardsFromArray = (prevArray) => { 
                    return prevArray.map((item) => { 
                        if (item.id === firstCard?.id || item.id === secondCard?.id) { 
                            return { ...item, isMatched: true }; 
                        } else { 
                            return item; 
                        } 
                    }); 
                }

                setCardsArray(removeCardsFromArray(cardsArray)); 
                removeSelection(); 
            } else { 
                const changeIsWrongForSelectedCards = (arr, toValue) => {
                    return arr.map((item) => { 
                        if (item.id === firstCard?.id || item.id === secondCard?.id) { 
                            return { ...item, isWrong: toValue }; 
                        } else { 
                            return item; 
                        } 
                    }); 
                }

                setCardsArray(changeIsWrongForSelectedCards(cardsArray, true))
                setToDisable(true);

                setTimeout(() => {
                    setToDisable(false);
                    setCardsArray(changeIsWrongForSelectedCards(cardsArray, false))
                    removeSelection(); 
                }, 3000); 


            } 
        } 
    }, [firstCard, secondCard]); 

    return (
        <div className="container"> 
            <div className="header"> 
                <h1>Capital Quiz</h1> 
            </div> 
            <div className="board"> 
                {
                    cardsArray?.map((item) => (
                        (!item.isMatched) ?
                        <Card 
                            item={item} 
                            key={item.id} 
                            handleSelectedCards={handleSelectedCards} 
                            isFirstCard={item === firstCard} 
                            isWorngCard={item.isWrong}
                            isDisabled={toDisable}
                        /> : null
                    )) 
                } 
            </div> 

            {(cardsArray?.filter((item) => { return !item.isMatched }).length == 0) ? ( 
               <div className="comments"> 
               Congratulations!
           </div> 
            ) : ( 
                <div></div>
            )} 
        </div> 
      ); 
}

export default QuizBoard;
